  and studentId = #{studentId,jdbcType=VARCHAR}


  and ownerId = #{ownerId,jdbcType=VARCHAR}


  and (studentId like '%' #{search,jdbcType=VARCHAR} '%' or
  ownerId like '%' #{search,jdbcType=VARCHAR} '%' or
