mapper.select:
select * from CourseView where 1 = 1
and status = #{status,jdbcType=VARCHAR}
and ( crn like '%' #{search,jdbcType=VARCHAR} '%' or name like '%' #{search,jdbcType=VARCHAR} '%')

select count(*) as count from CourseView 1 = 1
and facultyid = #{facultyid,jdbcType=VARCHAR}
and info = #{info,jdbcType=VARCHAR}
and (crn like '%' #{search,jdbcType=VARCHAR} '%' or name like '%' #{search,jdbcType=VARCHAR} '%' or credits like '%' #{search,jdbcType=VARCHAR} '%' or coulev like '%' #{search,jdbcType=VARCHAR} '%' or cousec like '%' #{search,jdbcType=VARCHAR} '%' or day like '%' #{search,jdbcType=VARCHAR} '%' or time like '%' #{search,jdbcType=VARCHAR} '%' or capa like '%' #{search,jdbcType=VARCHAR} '%' or remain like '%' #{search,jdbcType=VARCHAR} '%' or status like '%' #{search,jdbcType=VARCHAR} '%' or faculty like '%' #{search,jdbcType=VARCHAR} '%' or date like '%' #{search,jdbcType=VARCHAR} '%' or updatetime like '%' #{search,jdbcType=VARCHAR} '%')

select * from CourseView 1 = 1
and facultyid = #{facultyid,jdbcType=VARCHAR}
and info = #{info,jdbcType=VARCHAR}
and (crn like '%' #{search,jdbcType=VARCHAR} '%' or name like '%' #{search,jdbcType=VARCHAR} '%' or credits like '%' #{search,jdbcType=VARCHAR} '%' or coulev like '%' #{search,jdbcType=VARCHAR} '%' or cousec like '%' #{search,jdbcType=VARCHAR} '%' or day like '%' #{search,jdbcType=VARCHAR} '%' or time like '%' #{search,jdbcType=VARCHAR} '%' or capa like '%' #{search,jdbcType=VARCHAR} '%' or remain like '%' #{search,jdbcType=VARCHAR} '%' or status like '%' #{search,jdbcType=VARCHAR} '%' or faculty like '%' #{search,jdbcType=VARCHAR} '%' or date like '%' #{search,jdbcType=VARCHAR} '%' or updatetime like '%' #{search,jdbcType=VARCHAR} '%')
order by ${orderColumn} ${order} limit #{currentIndex,jdbcType=INTEGER},#{pageSize,jdbcType=INTEGER}
