mapper.insert:
insert into Transcript (id, studentid, crn, 
  grade, complete, assigntime, operator
  )
values (#{id,jdbcType=INTEGER}, #{studentid,jdbcType=VARCHAR}, #{crn,jdbcType=VARCHAR}, 
  #{grade,jdbcType=VARCHAR}, #{complete,jdbcType=VARCHAR}, #{assigntime,jdbcType=VARCHAR},#{operator,jdbcType=VARCHAR}
  )

mapper.update:
update Transcript
set grade = #{grade,jdbcType=VARCHAR},
  complete = #{complete,jdbcType=VARCHAR},
  assigntime = #{assigntime,jdbcType=VARCHAR},
  operator = #{operator,jdbcType=VARCHAR}
where studentid = #{studentid,jdbcType=VARCHAR} and crn = #{crn,jdbcType=VARCHAR}

mapper.select:
select count(*) as count from TranscriptView 1 = 1
and studentid = #{studentid,jdbcType=VARCHAR}
and crn = #{crn,jdbcType=VARCHAR}
and (studentid like '%' #{search,jdbcType=VARCHAR} '%' or crn like '%' #{search,jdbcType=VARCHAR} '%' or grade like '%' #{search,jdbcType=VARCHAR} '%' or complete like '%' #{search,jdbcType=VARCHAR} '%' or assigntime like '%' #{search,jdbcType=VARCHAR} '%' or slast like '%' #{search,jdbcType=VARCHAR} '%' or sfirst like '%' #{search,jdbcType=VARCHAR} '%' or operator like '%' #{operator,jdbcType=VARCHAR} '%' or facultyid like '%' #{search,jdbcType=VARCHAR} '%')

select * from TranscriptView 1 = 1
and studentid = #{studentid,jdbcType=VARCHAR}
and crn = #{crn,jdbcType=VARCHAR}
and (studentid like '%' #{search,jdbcType=VARCHAR} '%' or crn like '%' #{search,jdbcType=VARCHAR} '%' or grade like '%' #{search,jdbcType=VARCHAR} '%' or complete like '%' #{search,jdbcType=VARCHAR} '%' or assigntime like '%' #{search,jdbcType=VARCHAR} '%' or slast like '%' #{search,jdbcType=VARCHAR} '%' or sfirst like '%' #{search,jdbcType=VARCHAR} '%' or operator like '%' #{operator,jdbcType=VARCHAR} '%' or facultyid like '%' #{search,jdbcType=VARCHAR} '%')
order by ${orderColumn} ${order} limit #{currentIndex,jdbcType=INTEGER},#{pageSize,jdbcType=INTEGER}
