/*!50003 CREATE DEFINER=`root`@`%` FUNCTION `get_complete_credits`(sid varchar(100)) RETURNS int(11)
BEGIN



	DECLARE credits DOUBLE DEFAULT 0.0;



	DECLARE counts INT DEFAULT 0;







